def find_first_and_last_blue_points(grid: np.ndarray) -> Tuple[int, int, int, int]:
    rows, cols = grid.shape
    x1, y1, x2, y2 = (-1, -1, -1, -1)
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == blue:
                if x1 == -1:
                    x1, y1 = (i, j)
                x2, y2 = (i, j)
    return (x1, y1, x2, y2)

