def generate_blue_coordinates(grid: np.ndarray) -> List[Tuple[int, int]]:
    rows, cols = grid.shape
    blue_coordinates = []
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == blue:
                blue_coordinates.append((i, j))
    return blue_coordinates

