def find_red_pixels(input_grid: np.ndarray) -> List[Tuple[int, int]]:
    red_coordinates = []
    for i in range(input_grid.shape[0]):
        for j in range(input_grid.shape[1]):
            if input_grid[i, j] == red:
                red_coordinates.append((i, j))
    return red_coordinates

