def find_colors(input_grid: np.ndarray) -> List[int]:
    colors = set()
    for i in range(input_grid.shape[0]):
        for j in range(input_grid.shape[1]):
            if input_grid[i, j] != red and input_grid[i, j] != black:
                colors.add(input_grid[i, j])
    return list(colors)
